function approveSeller(a,b){var c=$("#approve-url").val();button=$("#approve-button-"+a),$.get(c,{seller_id:a,action:b}).done(function(){b==="approve"?($(button).removeClass("approve-seller").removeClass("btn-success"),$(button).removeClass("btn").html("Approved").unbind()):b==="unapprove"&&($(button).removeClass("unapprove-seller").removeClass("btn-danger"),$(button).removeClass("btn").html("Unapproved").unbind())})}$(".title-row").click(function(){$(this).toggleClass("highlight"),$(this).next(".actions-row").toggle()}),$(".approve-seller").click(function(){var a=$(this).attr("data-seller-id");approveSeller(a,"approve")}),$(".unapprove-seller").click(function(){if(confirm("Deactivate This Seller's Account?")){var a=$(this).attr("data-seller-id");approveSeller(a,"unapprove")}})