function approveProduct(a,b,c){var d=$("#approve-url").val();$.get(d,{product_id:a,action:b}).done(function(){b==="approve"||b==="delete"?c.slideUp("slow",function(){$(this).remove()}):b==="hold"&&(c.closest(".title-row").appendTo("#purgatory"),c.closest(".actions-row").appendTo("#purgatory"))})}function rateProduct(a,b,c,d){var e=$("#rate-url").val();$.get(e,{product_id:a,subject:b,value:c}).error(function(){d.closest("div").removeClass("chosen"),d.siblings("span").removeClass("selected")}).done(function(){d.closest("div").addClass("chosen"),d.siblings("span").removeClass("selected"),d.addClass("selected")})}$(".title-row").click(function(){$(this).toggleClass("highlight"),$(this).next(".actions-row").toggle()}),$(".approve-product").click(function(){var a=$(this).attr("data-product-id"),b=$(this).closest("tr");approveProduct(a,"approve",b)}),$(".hold-product").click(function(){var a=$(this).attr("data-product-id"),b=$(this).closest("tr");approveProduct(a,"hold",b)}),$(".delete-product").click(function(){if(confirm("Permanently Delete Product?")){var a=$(this).attr("data-product-id"),b=$(this).closest("tr");approveProduct(a,"delete",b)}}),$(".rate").click(function(){var a=$(this).attr("data-product-id"),b=$(this).attr("data-subject"),c=$(this).attr("data-value"),d=$(this).parent("span");c>0&&rateProduct(a,b,c,d)})