(function(){var controllers=angular.module("market-controllers",[]);controllers.controller("MarketController",["$http",function($http){var market=this;market.blocks=[];function big_or_small(){if(Math.floor(Math.random()*2)==1){return"double"}else{return"single"}}market.blocks=[{type:"category",id:"11"}];$http.get("/api/listings/?limit=6").success(function(data){for(listing=0;listing<data.results.length;listing++){market.blocks.push({type:"listing",size:"single",data:data.results[listing]})}market.next=data.next;market.prev=data.previous})}]);controllers.controller("CategoryController",["$http",function($http){var category=this;category.pages=1;category.listings=[];$http.get("/api/listings/").success(function(data){var page_size=24;category.pages=parseInt((data.count+(page_size-1))/page_size);category.listings=data.results})}]);controllers.controller("ListingController",["$http","$routeParams",function($http,$routeParams){var phone=this;$http.get("/api/listings/"+$routeParams.listingId).success(function(data){phone.detail=data})}])})();